catalog_script_client.script:
function onSubmit() {

    //Type appropriate comment here, and begin script below
    var urlParams = new URLSearchParams(top.window.location.search);
    var sysId = urlParams.get('req_item_id');
    //alert(sysId);
    if (sysId != null) {
        var question1 = g_form.getValue("select_one");
        var question2 = g_form.getValue("who");
        var question3 = g_form.getValue("input_requirements");

        var ga = new GlideAjax('SubmitGenerator');
        ga.addParam('sysparm_name', 'generate');
        ga.addParam('sysparm_sysId', sysId);
        ga.addParam('sysparm_squestion1', question1);
        ga.addParam('sysparm_squestion2', question2);
        ga.addParam('sysparm_squestion3', question3);

        ga.getXMLAnswer(callBack);
        return false;
    }
    function callBack(response) {
        //alert(response);
        if (response === "true") {
            var URL = "sp?id=index";
            top.window.onbeforeunload = null;
            top.window.onunload = null;
            top.window.location = URL;
        }
    }
}
