sys_script_include.script:
var GetSpecificationInfo = Class.create();
GetSpecificationInfo.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	getInfos : function() {
		var modelId = new GlideRecord('x_1755675_laptop_0_laptop_request_info');
		modelId.addQuery('model',this.getParameter('sysparm_model'));
		modelId.query();
		if(modelId.next()){
            var model = modelId.model.model;
			var ret = new GlideRecord('x_1755675_laptop_0_laptop_inventory');
			ret.addQuery('model',model);
			ret.query();
			if(ret.next()) {
				return ret.specifications;
			}
       }
	},
    type: 'GetSpecificationInfo'
});
