sys_script_include.script:
var GetValueUtils = Class.create();
GetValueUtils.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    getPreviousValues: function(){
        var sysId = this.getParameter('sysparm_sysId');

        var gr = new GlideRecord('sc_req_item');
        gr.addQuery('number', sysId);
        var result ={};
        gr.query();
        if (gr.next()) {
            result.question1=gr.variables.select_one +'';
            result.question2=gr.variables.who +'';
            result.question3=gr.variables.input_requirements +'';
            //gs.error('gr.variables===========' + result);
            return JSON.stringify(result);
        }else{
             return 'false';
         }
    },
    type: 'GetValueUtils'
});
