sys_ui_page.client_script:
function onCancel() {
	GlideModal.get().destroy();
	return false;
} 

function onSubmit() {
	alert('aaaa' + g_form.getDisplayValue('model'));
	var infos = new GlideAjax('GetSpecificationInfo');
	infos.addParam('sysparm_name', 'getInfos');
	infos.addParam('sysparm_model', g_form.getDisplayValue('model'));
	alert('aaaa' + g_form.getDisplayValue('model'));
    infos.getXML(returnInfos);
	GlideModal.get().destroy();
	return false;
}
function returnInfos(response) {
	var ret = response.responseXML.documentElement.getAttribute('answer');
	g_form.clearValue('specifications');
	g_form.setValue('specifications', ret);
}
